import React, {useEffect, useState} from 'react'

const Input = () => {
    const [inputStr, setinputStr] = useState("")
    const [itemInfo, setItemInfo] = useState({
      functionalName: "",
      variant: "",
      sub_brandName: "",
      brandName: "",
    })
    const [items, setItems] = useState({
      itemArray: [],
    })

    const debounce = (fn, delay) => {
      var timer;
      return (e)=>{
        if(timer) {
          clearTimeout(timer);
        }
        timer = setTimeout(() => {
          fn(e);
        }, delay);
      }
    }

    const submitReq = (e) => {
      var keyWord = e.target.value;
      if (keyWord.length >= 2) {        
        setinputStr(keyWord);
        setItemInfo({...itemInfo, [e.target.name]: e.target.value})
        console.log(itemInfo)
      } else {
        console.log("too short, not valid")
      }
    }

    var inputDebounceFn = debounce(submitReq, 1000);

    //



    const handleClick = () => {
      // fetch('/fpim')
      // .then(res => console.log(res.json()))
      // .then(items => setItems(items))
      // console.log(itemInfo)
      console.log()
    }

    useEffect(async ()=>{
      const response = await fetch('/fpim/');
      const data = await response.json();
      console.log(data)

      // TODO: Push Array
      setItems([...items, data])
    })

    return (
        <div>
          <div className="inputContainer">
            <div>
                Functional Name: {itemInfo.functionalName} <br/>
                <input className="input" name="functionalName" onKeyUp={(e) => {inputDebounceFn(e)}}></input>
            </div>
            <div>
                Variant: {itemInfo.variant} <br/>
                <input className="input" name="variant" onKeyUp={(e) => {inputDebounceFn(e)}}></input>
            </div>
            <div>
                Sub Brand Name: {itemInfo.sub_brandName} <br/>
                <input className="input" name="sub_brandName" onKeyUp={(e) => {inputDebounceFn(e)}}></input>
            </div>
            <div>
                Brand Name: {itemInfo.brandName} <br/>
                <input className="input" name="brandName" onKeyUp={(e) => {inputDebounceFn(e)}}></input>
            </div>
            <button onClick={handleClick}>Check</button> 
          </div>
          <div className="resultContainer">
            items:
            {items}
          </div>
        </div>
    )
}

export default Input
