import React, {useEffect, useState} from 'react'

const Input = () => {
      const [itemInfo, setItemInfo] = useState({
      functionalName: "",
      variant: "",
      sub_brandName: "",
      brandName: "",
    })
    const [items, setItems] = useState({
      data: [
        // {itemNumber:"313271",
        // itemDescription:"Catheter Balloon Drug-Eluting 2.0x30mm 140cm Fast-Exchange Pantera Lux Biotronik (EA=1)",
        // manufacturerPartNum:"365114",
        // functionalName:"Catheter Balloon Drug-Eluting",
        // variant:"2.0x30mm 140cm Fast-Exchange",
        // subBrandName:"Pantera Lux",
        // brandName:"Biotronik",
        // netContent:1}
      ]
    })

    const debounce = (fn, delay) => {
      var timer;
      return (e)=>{
        if(timer) {
          clearTimeout(timer);
        }
        timer = setTimeout(() => {
          fn(e);
        }, delay);
      }
    }

    const submitReq = (e) => {
      if (e.target.value.length >= 2) {        
        setItemInfo({...itemInfo, [e.target.name]: e.target.value})
      } else {
        console.log("too short, not valid")
      }
    }

    var inputDebounceFn = debounce(submitReq, 500);

    // Fire request here, use query
    console.log(itemInfo)
    const searchByKeyWords = async () => {
      var url = new URL('/fpim/query')
      var params = {functionalName: "banana"}
      url.search = new URLSearchParams(params).toString
      const response = await fetch(url)
    }

    //

    const handleClick = () => {
      searchByKeyWords()
    }

    useEffect(async ()=>{
      const response = await fetch('/fpim');
      const data = await response.json();
      setItems({data})
    },[])

    return (
        <div>
          <div className="inputContainer">
            <div>
                Functional Name: {itemInfo.functionalName} <br/>
                <input className="input" name="functionalName" onKeyUp={(e) => {inputDebounceFn(e)}}></input>
            </div>
            <div>
                Variant: {itemInfo.variant} <br/>
                <input className="input" name="variant" onKeyUp={(e) => {inputDebounceFn(e)}}></input>
            </div>
            <div>
                Sub Brand Name: {itemInfo.sub_brandName} <br/>
                <input className="input" name="sub_brandName" onKeyUp={(e) => {inputDebounceFn(e)}}></input>
            </div>
            <div>
                Brand Name: {itemInfo.brandName} <br/>
                <input className="input" name="brandName" onKeyUp={(e) => {inputDebounceFn(e)}}></input>
            </div>
            <button onClick={handleClick}>Check</button> 
          </div>
          <div className="resultContainer">
            <pre>{JSON.stringify(items.data)}</pre>
            {items.data ?
              <table>
              <thread>
              </thread>
                {items.data.map(element => 
                <tbody>
                  <td>{element.itemNumber}</td>
                  {/* <td>{element.itemDescription}</td> */}
                  <td>{element.manufacturerPartNum}</td>
                  <td>{element.functionalName}</td>
                  <td>{element.variant}</td>
                  <td>{element.sub_brandName}</td>
                  <td>{element.brandName}</td>
                  <td>{element.netContent }</td>
                </tbody>
                )}
            </table>
            :
            <div>you did not</div>}
          </div>
        </div>
    )
}

export default Input
