import React, {useState} from 'react'

const Input = () => {
    const [inputStr, setinputStr] = useState("")
    const [itemInfo, setItemInfo] = useState({
      functionalName: "",
      variant: "",
      sub_brandName: "",
      brandName: "",
    })

    const debounce = (fn, delay) => {
      var timer;
      return (e)=>{
        if(timer) {
          clearTimeout(timer);
        }
        timer = setTimeout(() => {
          fn(e);
        }, delay);
      }
    }

    const submitReq = (e) => {
      var keyWord = e.target.value;
      if (keyWord.length >= 2) {
        // TODO: Fire the request
        setinputStr(keyWord);
        console.log(e.target.value)
        console.log(e.target.name)
        // switch(e.target.name) {
        //   case 'functionalName':
        //     setItemInfo({

        //     })
        // }
        setItemInfo({
          [e.target.name]: e.target.value
        })
      } else {
        console.log("too short, not valid")
      }
    }

    var inputDebounceFn = debounce(submitReq, 1000);

    //
    const handleClick = () => {
      fetch('/fpim')
      .then(res => console.log(res.json()))
    }

    return (
        <div>
          <div className="inputContainer">
            <div>
            {itemInfo}

                Functional Name: {inputStr} <br/>
                <input className="input" name="functionalName" onKeyUp={(e) => {inputDebounceFn(e)}}></input>
            </div>
            <div>
                Variant: {inputStr} <br/>
                <input className="input" name="variant" onKeyUp={(e) => {inputDebounceFn(e)}}></input>
            </div>      <div>
                Sub Brand Name: {inputStr} <br/>
                <input className="input" name="sub_brandName" onKeyUp={(e) => {inputDebounceFn(e)}}></input>
            </div>      <div>
                Brand Name: {inputStr} <br/>
                <input className="input" name="brandName" onKeyUp={(e) => {inputDebounceFn(e)}}></input>
            </div>
            <button onClick={handleClick}>show all</button>
          </div>
        </div>
    )
}

export default Input
