import React, {useState} from 'react'

const Input = () => {
    const [inputStr, setinputStr] = useState("")

    const debounce = (fn, delay) => {
      var timer;
      return (e)=>{
        if(timer) {
          clearTimeout(timer);
        }
        timer = setTimeout(() => {
          fn(e);
        }, delay);
      }
    }

    const submitReq = (e) => {
      var keyWord = e.target.value;
      if (keyWord.length >= 2) {
        // TODO: Fire the request
        setinputStr(keyWord);
        console.log(e.target.value)
      } else {
        console.log("too short, not valid")
      }
    }

    var inputDebounceFn = debounce(submitReq, 1000);

    //
    const handleClick = () => {
      fetch('/')
      .then(res => console.log(res))
    }

    return (
        <div>
            <div>
                Functional Name: {inputStr} <br/>
                <input className="input" onKeyUp={(e) => {inputDebounceFn(e)}}></input>
            </div>
            <div>
                Variant: {inputStr} <br/>
                <input className="input" onKeyUp={(e) => {inputDebounceFn(e)}}></input>
            </div>      <div>
                Sub Brand Name: {inputStr} <br/>
                <input className="input" onKeyUp={(e) => {inputDebounceFn(e)}}></input>
            </div>      <div>
                Brand Name: {inputStr} <br/>
                <input className="input" onKeyUp={(e) => {inputDebounceFn(e)}}></input>
            </div>
            <button onClick={handleClick}>show all</button>
        </div>
    )
}

export default Input
